let defaultState = {
  forecast: {},
  suggestion: {}
}

export function reducer(state=defaultState, action){
  console.log(action);
  switch (action.type) {
    case "SET_USER":
      return {...state, selectedPlayer: action.payload }
      break;
    case "UNSELECT_PLAYER":
      return {...state, selectedPlayer: null}
    default: return state

  }
}
