import React, { Component } from 'react';
import {connect} from 'react-redux'

class LogInForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: ""
    };
  }

  componentDidMount() {
    const token = localStorage.token;
    fetch("http://localhost:3000/account", {
      method: "GET",
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(resp => resp.json())
      .then(data => {
        if (!data.message) {
          this.setState({
            currentUser: data
          });
        }else{
          this.setState({
            currentUser: null
          });
        }
      });
  }

  handleChanges = (e) =>{
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  logIn = e => {
   e.preventDefault();

   fetch("http://localhost:3000/login", {
     method: "POST",
     body: JSON.stringify({
       email: this.state.email,
       password: this.state.password
     }),
     headers: {
       "Content-Type": "application/json"
     }
   })
     .then(resp => resp.json())
     .then(data => {
       if (!data.message) {
         localStorage.token = data.token;
         this.setState({
           currentUser: data.user
         });
       } else {
         alert(data.message)
       }
     });
 };

  render() {
    console.log(this.state);
    return (
      <form onSubmit={this.logIn}>
        <input type="text" name="email" placeholder="email" onChange={e=>this.handleChanges(e)}/>
        <input type="password" name="password" placeholder="password" onChange={e=>this.handleChanges(e)}/>
        <input type="submit"/>
      </form>
    );
  }
}

function mapDispatchToProps(dispatch){
  return {
    currentUser: () => dispatch({type: "UNSELECT_PLAYER"}),

  }
}

export default connect(null, mapDispatchToProps)(LogInForm)
